<script>
function convertpdf()
{
	
	var all=document.getElementsByClassName("ui-draggable item")
	//alert(all.length)
	
	if(all.length<=0)
	{
		alert("please add minimum one shape")
		return
	}
	var data="count="+all.length
	for(var i=0;i<all.length;i++)
	{
		
		degree=getRotationDegrees(all[i],i)
		data+="&src"+i+"="+all[i].src
		data+="&top"+i+"="+all[i].style.top
		data+="&left"+i+"="+all[i].style.left
		data+="&degree"+i+"="+degree
	}
	var len=data.length-1
	//alert($("#models").width())
	//document.getElementById("test").value=data
	//return false
	//alert(data)
	 $.ajax({            			
            					url: "setvalues_gif.php",
								type: "POST",	        
								data: data,  
								cache: false,	
								success: function (html) {           
									  //if process.php returned 1/true (send mail success)
									//alert(html)
									window.location="pdfout_gif.php"
									 //window.location="list"
                                         				 }
							  });
	
	//alert(all[0].style.transform)
	//document.getElementById().style.
}
function getRotationDegrees(obj1,no) {
	
	obj1.id="sh"+no
	var id="#"+obj1.id
	//alert(id)
	obj=$(id)
	//alert(obj.css("transform"));
   	var matrix = obj.css("-webkit-transform") ||
    obj.css("-moz-transform")    ||
    obj.css("-ms-transform")     ||
    obj.css("-o-transform")      ||
    obj.css("transform");
    if(matrix !== 'none') {
        var values = matrix.split('(')[1].split(')')[0].split(',');
        var a = values[0];
        var b = values[1];
        var angle = Math.round(Math.atan2(b, a) * (180/Math.PI));
    } else { var angle = 0; }
    return angle;
}
